  <script>
    let fieldName = document.getElementById("buy_itemName");
    let fieldPrice = document.getElementById("sellPrice");
    let fieldDescription = document.getElementById("scroll-content");


    let shipMenu = document.getElementById("subNavShip");
    let shipMenuText = document.getElementById("subNavShipText");
    let droneMenu = document.getElementById("subNavDrone");
    let droneMenuText = document.getElementById("subNavDroneText");
    let extrasMenu = document.getElementById("subNavExtras");
    let extrasMenuText = document.getElementById("subNavExtrasText");
    let boostersMenu = document.getElementById("subNavBoosters");
    let boostersMenuText = document.getElementById("subNavBoostersText");
    let designsMenu = document.getElementById("subNavDesigns");
    let designsMenuText = document.getElementById("subNavDesignsText");

    let itemContainer1 = document.getElementById("item_list");
    let itemContainer2 = document.getElementById("item_list2");
    let itemContainer3 = document.getElementById("item_list3");
    let itemContainer4 = document.getElementById("item_list4");
    let itemContainer5 = document.getElementById("item_list5");

    function clickItem(name, price, description, mount) {
      fieldName.innerHTML = name;
      fieldPrice.value = price;
      fieldDescription.innerHTML = description;
    }

    function changeSection(section) {
      switch (section) {
        case 'Ship':
          itemContainer1.style.display = "block";
          itemContainer2.style.display = "none";
          itemContainer3.style.display = "none";
          itemContainer4.style.display = "none";
          itemContainer5.style.display = "none";

          activeObject(shipMenu, shipMenuText, section);
          desactiveObject(droneMenu, droneMenuText, "Drone");
          desactiveObject(extrasMenu, extrasMenuText, "Extras");
          desactiveObject(boostersMenu, boostersMenuText, "Boosters");
          desactiveObject(designsMenu, designsMenuText, "Designs");
          break;
        case 'Drone':
          itemContainer1.style.display = "none";
          itemContainer2.style.display = "block";
          itemContainer3.style.display = "none";
          itemContainer4.style.display = "none";
          itemContainer5.style.display = "none";

          activeObject(droneMenu, droneMenuText, section);
          desactiveObject(shipMenu, shipMenuText, "Ship");
          desactiveObject(extrasMenu, extrasMenuText, "Extras");
          desactiveObject(boostersMenu, boostersMenuText, "Boosters");
          desactiveObject(designsMenu, designsMenuText, "Designs");

          break;
        case 'Extras':
          itemContainer1.style.display = "none";
          itemContainer2.style.display = "none";
          itemContainer3.style.display = "block";
          itemContainer4.style.display = "none";
          itemContainer5.style.display = "none";

          activeObject(extrasMenu, extrasMenuText, section);
          desactiveObject(shipMenu, shipMenuText, "Ship");
          desactiveObject(boostersMenu, boostersMenuText, "Boosters");
          desactiveObject(designsMenu, designsMenuText, "Designs");
          desactiveObject(droneMenu, droneMenuText, "Drone");
          break;
        case 'Boosters':
          itemContainer1.style.display = "none";
          itemContainer2.style.display = "none";
          itemContainer3.style.display = "none";
          itemContainer4.style.display = "block";
          itemContainer5.style.display = "none";
          activeObject(boostersMenu, boostersMenuText, section);
          desactiveObject(shipMenu, shipMenuText, "Ship");
          desactiveObject(designsMenu, designsMenuText, "Designs");
          desactiveObject(droneMenu, droneMenuText, "Drone");
          desactiveObject(shipMenu, shipMenuText, "Ship");
          desactiveObject(extrasMenu, extrasMenuText, "Extras");
          break;
        case 'Designs':
          itemContainer1.style.display = "none";
          itemContainer2.style.display = "none";
          itemContainer3.style.display = "none";
          itemContainer4.style.display = "none";
          itemContainer5.style.display = "block";
          activeObject(designsMenu, designsMenuText, section);
          desactiveObject(shipMenu, shipMenuText, "Ship");
          desactiveObject(droneMenu, droneMenuText, "Drone");
          desactiveObject(shipMenu, shipMenuText, "Ship");
          desactiveObject(extrasMenu, extrasMenuText, "Extras");
          desactiveObject(boostersMenu, boostersMenuText, "Boosters");
          break;
      }
    }

    function activeObject(object, objectText, section) {
      objectText.classList.remove("subNavTextInactive" + section);
      objectText.classList.add("subNavTextActive" + section);
      object.classList.remove("subNavButtonRollover");
      object.classList.add("subNavButtonActive");
    }

    function desactiveObject(object, objectText, section) {
      objectText.classList.remove("subNavTextActive" + section);
      objectText.classList.add("subNavTextInactive" + section);
      object.classList.remove("subNavButtonActive");
      object.classList.add("subNavButtonInactive");
    }
  </script>
